import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

contract MerkleAirdrop {
    address public immutable token; // SPX token address
    bytes32 public immutable merkleRoot; // Merkle root from off-chain CSV

    // Bitmap to track claimed indexes
    mapping(uint256 => uint256) private claimedBitMap;

    event Claimed(
        uint256 indexed index,
        address indexed account,
        uint256 amount
    );

    constructor(address _token, bytes32 _merkleRoot) {
        token = _token;
        merkleRoot = _merkleRoot;
    }

    function isClaimed(uint256 index) public view returns (bool) {
        uint256 wordIndex = index / 256;
        uint256 bitIndex = index % 256;
        uint256 word = claimedBitMap[wordIndex];
        uint256 mask = (1 << bitIndex);
        return word & mask == mask;
    }

    function _setClaimed(uint256 index) private {
        uint256 wordIndex = index / 256;
        uint256 bitIndex = index % 256;
        claimedBitMap[wordIndex] |= (1 << bitIndex);
    }

    function claim(
        uint256 index,
        address account,
        uint256 amount,
        bytes32[] calldata merkleProof
    ) external {
        require(!isClaimed(index), "Already claimed");

        // Create the leaf hash using abi.encodePacked
        bytes32 node = keccak256(abi.encodePacked(index, account, amount));

        // Verify the proof
        require(
            MerkleProof.verify(merkleProof, merkleRoot, node),
            "Invalid Merkle Proof"
        );

        // Mark as claimed
        _setClaimed(index);

        // Transfer tokens
        require(
            IERC20(token).transfer(account, amount),
            "Token transfer failed"
        );

        emit Claimed(index, account, amount);
    }
}

